INSERT INTO Review (id, bookId, rating, comment, createdAt) SELECT RANDOM_UUID() ,CASE FLOOR(RAND() * 10) WHEN 0 THEN 84 WHEN 1 THEN 2701 WHEN 2 THEN 1513 WHEN 3 THEN 16328 WHEN 4 THEN 2641 WHEN 5 THEN 100 WHEN 6 THEN 145 WHEN 7 THEN 37106 WHEN 8 THEN 43 ELSE 25344 END, 1 + FLOOR(RAND() * 5), CASE FLOOR(RAND() * 15) WHEN 0 THEN 'Absolutely brilliant!' WHEN 1 THEN 'Could not put it down.' WHEN 2 THEN 'Highly recommend this book.' WHEN 3 THEN 'Waste of time, unfortunately.' WHEN 4 THEN 'Good but not great.' WHEN 5 THEN 'Amazing story and characters.' WHEN 6 THEN 'Disappointing ending.' WHEN 7 THEN 'Worth reading if you have time.' WHEN 8 THEN 'Best book I read this year!' WHEN 9 THEN 'Not what I expected.' WHEN 10 THEN 'A classic for a reason.' WHEN 11 THEN 'Overrated in my opinion.' WHEN 12 THEN 'Beautiful writing style.' WHEN 13 THEN 'Engaging from start to finish.' ELSE 'Struggled to finish it.' END, DATEADD('DAY', FLOOR(RAND() * DATEDIFF('DAY', DATE '2025-01-01', CURRENT_DATE())), DATE '2025-01-01') FROM SYSTEM_RANGE(1, 100);
